import React from 'react';
import PropTypes from 'prop-types';
import {FlexRow, FlexColumn} from "../Elements/Flex";
import styled from 'styled-components'
import App from "../../App";

const Post = styled(FlexColumn)`
    background: white;
    border-radius: 6px;
    box-shadow: 1px 1px 5px #00000024;
`;

const Img = styled.div.attrs({
    url: props => props.url
})`
    background: url(${props => props.url});
    background-size: contain;
    background-repeat: no-repeat;
    width: 25px;
    height: 25px;
    border-radius: 50%;
    margin: 0 10px;
`;

const PostContainer = props => {
    const {post} = props;
    console.log(post);
    return (
        <Post alignCenter justifyBetween>
            <FlexRow alignCenter height="50px" width="full">
                <Img url={post.thumbnailUrl}/>
                <b>{post.username}</b>
            </FlexRow>

            <FlexRow>
                <img src={post.imgUrl}/>
            </FlexRow>
        </Post>
    );
};

/*
PostContainer: PropTypes.shape({
    username: PropTypes.string.isRequired,
    thumbnailUrl: PropTypes.string.isRequired,
    imgUrl: PropTypes.string.isRequired,
    likes: PropTypes.number.isRequired,
    timestamp: PropTypes.string.isRequired,
    comments: PropTypes.array.isRequired,
}),
*/

export default PostContainer;
